-- [Auto Bounty Core Logic]
function AutoCombo(player)
    if not player then return end
    -- Lock target
    AimAt(player.Character.Head)
    -- Skill Combo Sequence
    UseSkill("Melee", "Z")
    wait(0.01)
    UseSkill("Melee", "X")
    wait(0.01)
    UseSkill("Melee", "C")
    wait(0.01)
    UseSkill("Sword", "Z")
    wait(0.01)
    UseSkill("Sword", "X")
    wait(0.01)
    UseSkill("Gun", "Z")
    wait(0.01)
    UseSkill("Gun", "X")
    wait(0.01)
    UseSkill("Fruit", "Z")
    wait(0.01)
    UseSkill("Fruit", "X")
    wait(0.01)
    UseSkill("Fruit", "C")
    wait(0.01)
    UseSkill("Fruit", "V")
    wait(0.01)
    UseSkill("Fruit", "F")
    wait(0.01)
    -- Default Attack
    RepeatMeleeAttack(player)
end

-- Tween TP
function TweenToPlayer(target)
    if not target then return end
    local TweenService = game:GetService("TweenService")
    local HumanoidRootPart = game.Players.LocalPlayer.Character:WaitForChild("HumanoidRootPart")
    local goal = {}
    goal.CFrame = target.Character.HumanoidRootPart.CFrame + Vector3.new(0, 3, 0)
    local tweenInfo = TweenInfo.new(2, Enum.EasingStyle.Linear)
    local tween = TweenService:Create(HumanoidRootPart, tweenInfo, goal)
    tween:Play()
end

