    local goal = {}
    goal.CFrame = target.Character.HumanoidRootPart.CFrame + Vector3.new(0, 3, 0)
    local tweenInfo = TweenInfo.new(2, Enum.EasingStyle.Linear)
    local tween = TweenService:Create(HumanoidRootPart, tweenInfo, goal)
    tween:Play()
end

